       function vimeoLoadGaData() {
        var vimeoObj = this;
        var iframe   = vimeoObj.element;
        var label    = iframe.getAttribute("data-ga-label");
        var page     = "string" === typeof gaPageName && "" != gaPageName ? gaPageName : "Unknown Page";
        
        vimeoObj.getVideoId().then(function (videoId) {
            vimeoObj.sessKey = videoId + "_" + page.replace(" | ", "_");
            vimeoObj.sessKey = vimeoObj.sessKey.toLowerCase();
        });
        
        if (label) {        
            vimeoObj.gaLabel = page + " | " + label;
        } else {
            vimeoObj.getVideoTitle().then(function (title) {                
                vimeoObj.gaLabel = page + " | " + title;
            });
        }
        
        vimeoObj.gaEventStates = {
