    function vimeoTriggerGaEvent(action) {
        var vimeoObj = this;
        var iframe   = vimeoObj.element;
        var actions  = "play|end";
        var cat      = "PTP-Videos";
        var videoId  = iframe.getAttribute("id");
        
        if ("function" !== typeof ga) {
            console.error("[vimeoTriggerGaEvent] ga is not a function");
            return false;
        }
        
        if ( ! action) {
            console.error("[vimeoTriggerGaEvent] undefined action");
            return false;
        }
        
        action = action.toLowerCase();
    
        if (actions.indexOf(action) < 0) {
            console.error("[vimeoTriggerGaEvent] invalid action: " + action);
            return false;
        }
        
        var sessKey = "vimeo_" + vimeoObj.sessKey + "_" + action;
        
        if (this.gaEventStates[action] || sessionStorage.getItem(sessKey)) {
            console.log("[vimeoTriggerGaEvent] [" + videoId + "] [" + action + "] has already been triggered!", this.gaEventStates[action], sessionStorage.getItem(sessKey));
            return false;
        } else {
            console.log("[vimeoTriggerGaEvent] [" + videoId + "] [" + action + "] | start tracking event");
        }
    
        /*console.log(vimeoObj.gaLabel);*/
        this.gaEventStates[action] = true;
        sessionStorage.setItem(sessKey, true);
    
        if (isLiveServer) {
            ga("send", "event", cat, action, vimeoObj.gaLabel);
        }
        
        return true;
    }
    
