    window.loadApiForVimeoIframe = function(videoObj, autoPlay) {
        if ("object" !== typeof Vimeo || "object" !== typeof videoObj || "1" == videoObj.getAttribute("data-vimeo-loaded")) {
            return false;
        }
        
        var id = videoObj.getAttribute("id");    
        var vimeoObj = new Vimeo.Player(videoObj);

        vimeoObj.getVideoId().catch(function (err) {
            var altVideo = videoObj.getAttribute("alt-video");
            
            if ("string" === typeof altVideo && "" !== altVideo) {
                videoObj.setAttribute("src", altVideo);
                videoObj.setAttribute("use-alt-video", "true");
            }
        });
        
        vimeoObj.setLoop(false);
        vimeoObj.loadGaData();
        vimeoObj.on("loaded", function () {
            console.log("loaded vimeo! [" + id + "]");
            /*onVimeoStateChange(id, false);*/
            
            if (autoPlay) {
                vimeoObj.play();
            }
        });
        vimeoObj.on("bufferstart", function () {
            this.triggerGaPlay();
            console.log("bufferstart vimeo! [" + id + "]");
            onVimeoStateChange(id, true);
        });
        vimeoObj.on("play", function () {
            var played = vimeoObj.element.getAttribute("played");
            var vimeoTag = vimeoObj.element.getAttribute("play-event-mf-tag");
                console.log(played+"play"+vimeoTag);
            if (vimeoTag && ! played) {
                /*add Google Analytics events to videos*/
                if ("function" === typeof ga) {
                    if ("artist_people_video" == vimeoTag || "artist_pet_video" == vimeoTag) {
                         console.log("play1");
                        ga("send", "event", "Video-Play-Desktop", "Play", "Play the video on top desktop");
                    } else if ("artist_people_video_m" == vimeoTag || "artist_pet_video_m" == vimeoTag) {
                        ga("send", "event", "Video-Play-Mobile", "Play", "Play the video on top mobile");
                    }
                }

                if ("object" === typeof window._mfq) {
                    window._mfq.push(["tag", vimeoTag]);
                }
                vimeoObj.element.setAttribute("played", "1");
            }

            this.triggerGaPlay();
            console.log("played vimeo! [" + id + "]");
            onVimeoStateChange(id, true);
        });
        vimeoObj.on("pause", function () {
            console.log("paused vimeo! [" + id + "]");
            onVimeoStateChange(id, false);
        });
        vimeoObj.on("ended", function () {
            this.triggerGaEnd();
            console.log("ended vimeo! [" + id + "]");
            onVimeoStateChange(id, false);
        });
        
        Player[id] = vimeoObj;
        
        videoObj.setAttribute("data-vimeo-loaded", "1");
        
        if ( ! vimeoSetDefaultTextTrack(vimeoObj)) {
            Player[id]["itvSetTextTrack"] = setInterval(function () {
                console.log("itvSetTextTrack", "vimeo_id_" + id);
                if (vimeoSetDefaultTextTrack(Player[id])) {
                    clearInterval(Player[id]["itvSetTextTrack"]);
                }
            }, 1000);
        }
        
        return true;
    }
    
